mapper.select:
SELECT img_location
FROM nft_data_img
WHERE img_num=#{imgNum}

mapper.update:
UPDATE nft_data
SET del_yn = 'Y', nft_title = CONCAT('(삭제)', nft_title)
WHERE nft_num=#{nftNum}

mapper.select:
SELECT sel.* FROM (SELECT nd.nft_num, nd.user_num, nd.nft_title, nd.nft_id, nd.nft_publisher, nd.nft_owner, nd.nft_original_date, nd.hide_yn, CASE WHEN nd.del_yn='N' THEN '발행' ELSE '삭제' END AS del_yn, ndd.type_num FROM nft_data nd JOIN nft_data_detail ndd ON nd.detail_num = ndd.detail_num
WHERE nd.nft_title LIKE '%' || #{searchWord} || '%'
WHERE nd.nft_id LIKE '%' || #{searchWord} || '%'
WHERE nd.nft_original_date LIKE #{searchWord} || '%'
WHERE nd.del_yn = #{searchWord}
AND ndd.type_num = #{typeNum} ORDER BY 1 ) sel LIMIT #{listNum} OFFSET (#{pageNum}-1)*#{listNum}

SELECT COUNT(*) FROM nft_data nd JOIN nft_data_detail ndd ON nd.detail_num = ndd.detail_num
WHERE nd.nft_title LIKE '%' || #{searchWord} || '%'
WHERE nd.nft_id LIKE '%' || #{searchWord} || '%'
WHERE nd.nft_original_date LIKE #{searchWord} || '%'
WHERE nd.del_yn = #{searchWord}
AND ndd.type_num = #{typeNum}

SELECT nft_id
FROM nft_data
WHERE nft_num=#{nftNum}
